import React, { useEffect, useState, useCallback } from 'react';
import './App.css';
import useDerivWebSocket from './Websocket';
// Replace with your Deriv API token
const apiToken = 'YOUR_DERIV_API_TOKEN';
const symbol = 'R_100';

function Trade() {
  const { tick, error, ws } = useDerivWebSocket(apiToken, symbol);
  const [lastTick, setLastTick] = useState(null);

  const placeOrder = useCallback((contractType) => {
    if (!ws) return;

    const order = {
      buy: 1,
      price: 10, // Amount you want to trade
      parameters: {
        amount: 10, // Stake amount
        basis: 'stake', // Basis
        contract_type: contractType, // Contract type ('CALL' or 'PUT')
        currency: 'USD', // Currency
        duration: 1, // Duration
        duration_unit: 'm', // Duration unit
        symbol: symbol, // Symbol
      },
    };

    ws.send(JSON.stringify(order));
  }, [ws]);

  useEffect(() => {
    if (tick && lastTick) {
      const priceChange = tick.quote - lastTick.quote;

      if (priceChange > 0) {
        console.log('Price increased, considering buy...');
        placeOrder('CALL');
      } else if (priceChange < 0) {
        console.log('Price decreased, considering sell...');
        placeOrder('PUT');
      }
    }
    setLastTick(tick);
  }, [tick, lastTick, placeOrder]);

  return (
    <div className="App">
      <header className="App-header">
        <h1>Deriv Trading Bot</h1>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        {tick && (
          <div>
            <p>Symbol: {tick.symbol}</p>
            <p>Quote: {tick.quote}</p>
          </div>
        )}
      </header>
    </div>
  );
}

export default Trade;
